import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import * as auth from '../utils/auth.js';

function Login({handleLogin}){
// const Login = () => {
  const [formValue, setFormValue] = useState({
    password: "",
    email: "",
  });

const navigate = useNavigate();
const handleChange =(e) => {
    const {name, value} = e.target;

    setFormValue({
      ...formValue,
      [name]: value
    });
}
const handleSubmit = (e) => {
  e.preventDefault();
//   if (!formValue.email || !formValue.password){
//     return;
//   }
  auth.authorize(formValue.password, formValue.email)
    .then((data) => {
    //   if (data.jwt){
        handleLogin(data);

        // setFormValue({password: '', email: ''});
        navigate('/cards', {replace: true});
    //   }
    })
    .catch(err => console.log(err));
} 



return (
  <div className="register">
    <p className="register__name">Вход</p>
    <form className="register__form" onSubmit={handleSubmit}>
      <div className="register__input">
        <input
          id="email"
          type="email"
          name="email"
          value={formValue.email}
          onChange={handleChange}
          placeholder="Email"
          className="register__info"
          minLength="2"
          maxLength="40"
          required=""
        />
        <input
          id="password"
          type="password"
          name="password"
          value={formValue.password}
          onChange={handleChange}
          placeholder="Пароль"
          className="register__info"
          minLength="6"
          maxLength="40"
          required=""
        />
      </div>
      <button className="register__save" type="submit">
        Войти
      </button>
    </form>
    <div className="register__signin">
      <p className="register__signin-text">Ещё не зарегистрированы?</p>
      <Link to="/signup" className="register__signin-link">
        Регистрация
      </Link>
    </div>
  </div>
);
}
export default Login;
