import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import EditProfilePopup from "./EditProfilePopup";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";


function App() {
  const [currentUser, setCurrentUser] = React.useState("")
  const [cards, setCards] = React.useState([]);

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = React.useState(false)
  const [selectedCard, setSelectedCard] = React.useState({
    isOpen: false,
    item: {}
  });

  React.useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()
    ])
      .then(([userData, cards]) => {
        setCurrentUser(userData)
        setCards(cards);
      })
      .catch((err) => console.log(err));
  }, []);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  function handleDeleteCardClick() {
    setIsDeleteCardPopupOpen(true);
  }

  // закрытие всех попапов
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsDeleteCardPopupOpen(false)
    setSelectedCard({
        isOpen: false,
        item: {}
      });
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  } 

  function handleUpdateUser(name, about){
    api.editUserInfo(name, about)
    .then(({name, about, avatar, _id}) => {
      setCurrentUser({name, about, avatar});
        closeAllPopups();
        console.log(currentUser)
    }).catch(err => console.log(err))
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className="App">
      <body className="page">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onEditAvatar={handleEditAvatarClick}
          onAddPlace={handleAddPlaceClick}
          onCardClick={setSelectedCard}
          onCardDelete={handleDeleteCardClick}
          cards={cards}
          onCardLike={handleCardLike}
        />
        <Footer />
        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser}/> 
        <PopupWithForm
          name="ava"
          title="Обновить аватар"
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          btnText="Сохранить"
        >
          <div className="popup__input">
            <input
              id="ava-link-input"
              type="url"
              name="avatar"
              placeholder="https://somewebsite.com/someimage.jpg"
              className="popup__info popup__info_input_ava-link"
              required=""
            />
            <span className="ava-link-input-error popup__info-error">
              Необходимо ввести ссылку на фото
            </span>
          </div>
        </PopupWithForm>
        <PopupWithForm
          name="card"
          title="Новое место"
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          btnText="Создать"
        >
          <div className="popup__input">
            <input
              id="card-name-input"
              type="text"
              name="name"
              placeholder="Название"
              className="popup__info popup__info_input_card-name"
              minlength="2"
              maxlength="30"
              required=""
            />
            <span className="card-name-input-error popup__info-error">
              Необходимо напистаь название карточки
            </span>
          </div>
          <div className="popup__input">
            <input
              id="card-link-input"
              type="url"
              name="link"
              placeholder="Ссылка на картинку"
              className="popup__info popup__info_input_card-link"
              required=""
            />
            <span className="card-link-input-error popup__info-error">
              Необходимо ввести ссылку на фото
            </span>
          </div>
        </PopupWithForm>
        <PopupWithForm 
          name="delete" 
          title="Вы уверены?"
          isOpen={isDeleteCardPopupOpen}
          onClose={closeAllPopups}
          btnText="Да"
          />
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      </body>
    </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
